import {rootConstants} from '../_constants';

const initialState = {
  lessonData: null,
  counter: 247
}

export default function lessonReducer(state = initialState, action) {
  switch (action.type) {
    case rootConstants.ROOT_REQUEST:
        return { counter: action.data };
    case rootConstants.ROOT_SUCCESS:
        return { counter: 2223123123423 };
    case rootConstants.ROOT_FAIL:
      return {};
    default:
      return state
  }
}
