import { get } from 'axios';
import { call, cancel, cancelled, fork, put, take } from 'redux-saga/effects';
import {rootConstants} from '../_constants';
import qs from 'qs';
//Grapic hesgiin datag avah serveres avah jhuselt yvulah heseg
export function graphDataFetch (lessonName) 
{
    return new Promise(async (resolve, reject) => 
    {
        try 
        {  
            fetch('http://localhost:3001/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'
                },
                body: 	qs.stringify({'lessonname': lessonName})
            })
            .then((res) => res.json())
            .then((data) => {
                alert(JSON.stringify(data))
                resolve(data)})
        } 
        catch(error) 
        {
            alert("4 doh laert")
            reject(error);
        }
    });
}

export function* graphData(lessonName) {
    try 
    {
        const lessonDatas = yield call(graphDataFetch, lessonName)
        yield put({type: rootConstants.MODAL_TRUE ,data: lessonDatas})
    } 
    catch(error) 
    {
        alert("error")
    }
    finally 
    {
        if (yield cancelled()) 
        {
            alert("cancel modal data ")
        }
    }
}

export function* graphState() 
{
    while (true) 
    {
        const {data} = yield take(rootConstants.MODAL_REQUEST)
        yield put({type: rootConstants.MODAL_REQUEST ,data: data})
        const setData = yield fork(graphData, data)
    }
}

export function* logActions() 
{
    while (true) 
    {
      const action = yield take('*')
      console.log(action.type);
    }
}
