import React from 'react';
import ReactDOM from 'react-dom';
import ReactModal from 'react-modal';
import { connect } from 'react-redux';
import {rootConstants} from '../_constants';
import DataPrint from './dataPrint';
import AmountChart from '../_components/amountChart';

import './componentcss.css';
class ModalGraph extends React.Component {
    constructor () {
      super();
      this.state = {
        showModal: false
      };

      this.handleOpenModal = this.handleOpenModal.bind(this);
      this.handleCloseModal = this.handleCloseModal.bind(this);
    }

    handleOpenModal () {
      this.setState({ showModal: true });
    }

    handleCloseModal () {
      this.setState({ showModal: false });
    }

    render () {
      return (
        <div>
          <ReactModal 
             isOpen={this.props.islog}
             contentLabel="Дүнгийн график"
             className="Modal"
             overlayClassName="Overlay"
             onRequestClose={ () => this.props.modalFalse()}
             >
            <div>モーダル{[this.props.counter]}</div>
            <AmountChart></AmountChart>
            <button onClick={ () => this.props.modalFalse()}>Close Modal</button>
          </ReactModal>
        </div>
      );
    }
  }

  function mapStateToProps(state) {
    return {
       lessonData: state.lessonData,
       counter: state.counter,
       islog: state.islog

    }
 }
 function mapDispatchToProps(dispatch) {
    return {
        modalTrue: () => dispatch({type: rootConstants.MODAL_TRUE }),
        modalFalse: () => dispatch({type: rootConstants.MODAL_FALSE }),
    }
 }
  export default connect(mapStateToProps,mapDispatchToProps)(ModalGraph);
