import React from 'react';
import { Grid } from 'gymnast';
import { connect } from 'react-redux';
import Dataprint from './../_components/dataPrint';
import LessonData from '../_data/lessonData';
import DataPrints from './../_components/dataPrints';
const data11 = [];
const data22 = [];
const data33 = [];
const data44 = [];
const data55 = [];
class homeScreen extends React.Component {
    constructor() {
        super();
        this.handleClick = this.handleClick.bind(this);
        this.state = {
            data: {},
            data1: [],
            data2: [],
            data3: [],
            data4: [],
            data5: [],
            colors: 'white',
            colors1: 'green'

        }
     }
     componentDidMount() {

      for (var i = 0; i < LessonData.length; i++)
      {
            if(LessonData[i].level == 1 && LessonData[i].consumption != "МЭРГЭЖЛИЙН ХИЧЭЭЛ" ){
               data11.push(LessonData[i]);
            }
            if(LessonData[i].level == 2 && LessonData[i].consumption != "МЭРГЭЖЛИЙН ХИЧЭЭЛ" ){
               data22.push(LessonData[i]);
            }
            if(LessonData[i].level == 3 && LessonData[i].consumption != "МЭРГЭЖЛИЙН ХИЧЭЭЛ" ){
               data33.push(LessonData[i]);
            }
            if(LessonData[i].level == 4 && LessonData[i].consumption != "МЭРГЭЖЛИЙН ХИЧЭЭЛ" ){
               data44.push(LessonData[i]);
            }else{
               data55.push(LessonData[i]);
            }

      }

      this.setState({data1: data11, 
                     data2: data22,
                     data3: data33,
                     data4: data44,
                     data5: data44,})

     }

     handleClick(person, i) {
         this.setState({
            data: person,
            colors:"#00C49F" 
         })

      }
     render() {
        return (
         <Grid >
            <Grid className="colsheader" size={3}><h1>1 Күрсийн үзвэл зохих хичээл</h1></Grid>
            <Grid className="colsheader" size={3}><h1>2 Күрсийн үзвэл зохих хичээл</h1></Grid>
            <Grid className="colsheader" size={3}><h1>3 Күрсийн үзвэл зохих хичээл</h1></Grid>
            <Grid className="colsheader" size={3}><h1>4 Күрсийн үзвэл зохих хичээл</h1></Grid>

            <Grid className="cols" size={3} margin="2 2 2 0">
               {this.state.data1.map((person, i) => <Dataprint data={person}></Dataprint>)}
            </Grid>
            <Grid className="cols" size={3} margin="2 2 2 0">
               {this.state.data2.map((person, i) => <Dataprint data={person}></Dataprint>)}
            </Grid>
            <Grid className="cols" size={3} >
               {this.state.data3.map((person, i) => <Dataprint data={person}></Dataprint>)}
            </Grid>
            <Grid className="cols" size={3} >
               {this.state.data4.map((person, i) => <Dataprint data={person}></Dataprint>)}
            </Grid>

            <Grid className="colsheader" size={12}><h1>1 Күрсийн үзвэл зохих хичээл</h1></Grid>
            <Grid className="cols" size={12}>
               {this.state.data5.map((person, i) => <Dataprint data={person}></Dataprint>)}
            </Grid>
        <Grid className="colsheader" size={12}><h1>redux == {this.props.counter}</h1></Grid>
         </Grid>
        );
     }
}
function mapStateToProps(state) {
   return {
      lessonData: state.lessonData,
      counter: state.counter
   }
}

export default connect(mapStateToProps)(homeScreen);
