import React from 'react';
import './appcss.css';
import { Grid } from 'gymnast';
import { connect } from 'react-redux';
import Dataprint from './../_components/dataPrint';
import LessonDatas from '../_data/lessonData';
import ModalGraph from '../_components/modalgraph'
import {allActions} from '../_actions'
import ModalSchool from '../_components/modalSchool';
import ModalLesson from '../_components/modalLesson';


class homeScreen extends React.Component {
    constructor() {
        super();
        this.handleClick = this.handleClick.bind(this);
        this.state = {
            data: {},
            colors: 'white',
            colors1: 'green'

        }
     }



     handleClick() {
     }
     render() {
        return (
         <Grid className="App-header"> 
            <Grid className="colsheader" size={12}> 
               <h1>{this.props.counter}</h1><br></br>
               <h1>{this.props.schoolName}</h1><br></br>
               <h1>{JSON.stringify(this.props.lessonData)}</h1><br></br>
               <h1>{this.props.buttonStateName}</h1><br></br>
               <h1>{JSON.stringify(this.props.example)}</h1><br></br>



            </Grid>
            <Grid className="" size={12}> 
               <button className="myHeaderButton" onClick={() => this.props.buttonStateControl("GRAPH")}>GRAPH</button>
               <button className="myHeaderButton" onClick={() => this.props.buttonStateControl("Zalgamj")}>Zalgamj</button>
               <button className="myHeaderButton" onClick={() => this.props.buttonStateControl("Etseg")}>Etseg</button>
            </Grid>

            <Grid className="colsheader" size={3}> <ModalGraph></ModalGraph><ModalSchool></ModalSchool><ModalLesson></ModalLesson><h1>1 Күрсийн үзвэл зохих хичээл</h1></Grid>
            <Grid className="colsheader" size={3}><h1>2 Күрсийн үзвэл зохих хичээл</h1></Grid>
            <Grid className="colsheader" size={3}><h1>3 Күрсийн үзвэл зохих хичээл</h1></Grid>
            <Grid className="colsheader" size={3}><h1>4 Күрсийн үзвэл зохих хичээл</h1></Grid>

            <Grid className="cols"  size={3} margin="2 2 2 0">
               {this.props.allLessonData[0].data1.map((person, i) => <Dataprint data={person}></Dataprint>)}
            </Grid>
            <Grid className="cols" size={3} margin="2 2 2 0">
               {this.props.allLessonData[1].data2.map((person, i) => <Dataprint data={person}></Dataprint>)}
            </Grid>
            <Grid className="cols" size={3} >
               {this.props.allLessonData[2].data3.map((person, i) => <Dataprint data={person}></Dataprint>)}
            </Grid>
            <Grid className="cols" size={3} >
               {this.props.allLessonData[3].data4.map((person, i) => <Dataprint data={person}></Dataprint>)}
            </Grid>

            <Grid className="colsheader" size={12}><h1>choloot Күрсийн үзвэл зохих хичээл</h1></Grid>
            <Grid className="cols" size={12}>
               {this.props.allLessonData[4].data5.map((person, i) => <Dataprint data={person}></Dataprint>)}
            </Grid>


         </Grid>

        );
     }
}

export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(homeScreen);
