import {rootConstants} from '../_constants';

const initialState = {
  lessonData: [],
  counter: 247,
  islog: false
}

export default function lessonReducer(state = initialState, action) {
  switch (action.type) {
    case rootConstants.ROOT_REQUEST:
        return { counter: 1 ,lessonData: action.data };
    case rootConstants.ROOT_SUCCESS:
        return {counter: 400};
    case rootConstants.MODAL_TRUE:
        return { counter: 7700, islog: true};
    case rootConstants.MODAL_FALSE:
        return { counter: 999, islog: false};
    default:
      return state
  }
}
