import React from 'react';
import './componentcss.css';
import {connect} from 'react-redux';
import {rootConstants} from '../_constants';
import {allActions} from '../_actions'

class dataPrints extends React.Component {
   constructor() {
      super();
      this.handleClick = this.handleClick.bind(this);
      this.hoverOn = this.hoverOn.bind(this);
      this.hoverOff = this.hoverOff.bind(this);
      this.modalState = this.modalState.bind(this);
      this.state = {
         data: [],
         colors:"white",
         activeStatus: false
      }
   }
   handleClick(person) {
      alert("sdfsd")
   }
   hoverOn(data){
      if(this.state.activeStatus)
      {
         this.setState({ colors: "white" ,activeStatus: false});

      }else
      {
         this.setState({ colors: "blue" ,activeStatus: true});
         this.props.LessonDatas({"id":data})
      }

   }
   hoverOff(){
      this.setState({ colors: "white" });
   }

   modalState(){
      alert(JSON.stringify(this.state.data))
   }



   render() {

      return (
         <table style={{background: this.state.colors}} className={this.props.data.id}
                onClick={() => this.hoverOn(this.props.data.id)}>
            <tr style={{background: this.state.colors}} >
                  <td>{this.props.data.index}-</td>
                  <td>{this.props.data.lesson_name}-</td>
                  <td>({this.props.data.kridit})-</td>
                  <td>{this.props.data.season}</td>
                  <td>{JSON.stringify(this.props.lessonData)}</td>
            </tr>
         </table>
      );
   }
}
function mapStateToProps(state) {
   return {
      lessonData: state.lessonData,
      counter: state.counter,
      islog: state.islog,
      Reqislog: state.Reqislog,
      graphLessonName: state.graphLessonName,
      graphLessonData: state.graphLessonData,
      schoolName: state.schoolName,



   }
}
function mapDispatchToProps(dispatch) {
   return {
      LessonDatas: (data1) => dispatch({type: rootConstants.LESSONS_DATA , data: data1}),
   }
}



export default connect(mapStateToProps,mapDispatchToProps)(dataPrints);
