import React from 'react';
import './classCss.css';
import {  Link } from 'react-router-dom';
import {rootConstants} from '../_constants';
import { connect } from 'react-redux';
import  history  from '../_store/history';
import schoolData from '../_data/schoolData';
import PlanPrint from '../_components/_lessonPlan/planPrint';
import {allActions} from '../_actions'
import GridLayout from 'react-grid-layout';


class classScreen extends React.Component {

    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.state = {
          isState: false,
          isActive: true,

        };
      }
      componentDidMount() {
        this.props.lessonPlanRequest("hihah");

      }


      handleClick(data, islog) {
        if(islog){
          this.props.modalSchoolFalse(data)
        }else{
          this.props.newSchoolName(data)
        }

      }

      render() {
        var layout = [
          { i: 'a', x: 0, y: 0, w: 1, h: 2 },
          { i: 'b', x: 1, y: 0, w: 1, h: 2, minW: 2, maxW: 4 },
          { i: 'c', x: 2, y: 0, w: 1, h: 2 }
        ];
        return (
          <GridLayout 
            className="layout" 
            layout={layout} 
            cols={layout.length}
            rowHeight={30} 
            width={500}
            margin={[1,1]}
            containerPadding={[0,0]}
            >
            <Box key="a">a</Box>
            <Box key="b">b</Box>
            <Box key="c">c</Box>
          </GridLayout>
        );

    }
}


export default connect(allActions.mapStateToProps,allActions.mapDispatchToProps)(classScreen);
